package switchCase;

public class SwitchTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num=10; //declaring variable for switch
switch(num) {
//case statement

case 1: System.out.println("12");
break;

case 12: System.out.println("15");
break;

case 10:System.out.println("very good");
break;

case 15: System.out.println("20");
break;

//default case statement
default: System.out.println("number not valid");}
	}

}
